<script>

$(document).ready(function(){

	// Mouseover - Mouseleave für die Bildergalerie
	$('.row a')
		.mouseover(function() {
			$(this).children('img').css({
				'width' : '70px',
				'height' : '70px'
			});
		})
		.mouseleave(function() {
			$(this).children('img').css({
				'width' : '60px',
				'height' : '60px'	
			});
		});
	
	// Fancybox Anzeige 
	$('.fancybox_sb , .fancybox_es_1 , .fancybox_es_2 , .fancybox_es_3').fancybox({
		helpers : {
			thumbs : {
				width: 50,
				height: 50
			}
		}    
    });

	// Loading Div ausblenden
	$('#loading').hide();
});

</script>